import React from "react";
import {
  ScrollView,
  StyleSheet,
  Dimensions,
  Image,
  TouchableOpacity,
  Linking
} from "react-native";
import { Block, Text, theme } from "galio-framework";
import { useSafeArea } from "react-native-safe-area-context";
import Images from "../constants/Images";
import { DrawerItem as DrawerCustomItem, Icon } from "../components";

import nowTheme from "../constants/Theme";

const { width } = Dimensions.get("screen");

function CustomDrawerContent({
  drawerPosition,
  navigation,
  profile,
  focused,
  state,
  ...rest
}) {
  const insets = useSafeArea();
  const screens = [    
    {title: "HomePage", navigateTo: "HomeDrawer"},
    {title: "Farm Management", navigateTo: "FMDrawer"},
    {title: "Diagnose", navigateTo: "DDrawer"},
    {title: "Weather", navigateTo: "WDrawer"},
    {title: "Nursury Plants", navigateTo: "NPDrawer"},    
    {title: "Tracking", navigateTo: "TDrawer"},
    {title: "Doctor Online", navigateTo: "DODrawer"},
    {title: "Other", navigateTo: "ODrawer"},
    {title: "Logout", navigateTo: "LogoutDrawer"},   
    // "Chụp hình",
    // "Components",
    // "Articles",
    // "Profile",
    // "Account",
    // "Settings"
  ];
  return (
    <Block
      style={styles.container}
      forceInset={{ top: "always", horizontal: "never" }}
    >
      <Block style={styles.header}>
        <Image style={styles.logo} source={Images.Logo} />
        <Block right style={styles.headerIcon}>
          <Icon
            name="align-left-22x"
            family="NowExtra"
            size={15}
            color={"white"}
          />
        </Block>
      </Block>
      <Block flex style={{ paddingLeft: 8, paddingRight: 14, marginVertical: 30 }}>
        <ScrollView style={{ flex: 1 }} showsVerticalScrollIndicator={false}>
          {screens.map((item, index) => {
            return (
              <DrawerCustomItem
                title={item?.title}
                navigateTo={item?.navigateTo}
                key={index}
                navigation={navigation}
                focused={state.index === index ? true : false}
              />
            );
          })}
          <Block flex style={{ marginTop: 24, marginVertical: 8, paddingHorizontal: 8 }}>
          <Block
            style={{ borderColor: 'white', width: '93%', borderWidth: StyleSheet.hairlineWidth, marginHorizontal: 10}}
          />
        </Block>
        </ScrollView>
      </Block>
    </Block>
  );
}

